package first1.java;

public class SwitchCase {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int tech = 0;
		switch(tech){
		case 1:
		{
		System.out.println("java");
		break;
		}
		case 2:{
		System.out.println("ES6");
		break;
		}
		default:{
		System.out.println("Not Listed..");
		}
		}
	}
}


